import { defineComponent } from 'vue';
import { useCounterStore } from '@/stores/counter';
import { supabase } from "@/supabase";
import { IonContent } from '@ionic/vue';
import { IonCard, IonCardContent, IonCardHeader, IonCardTitle, IonButton, actionSheetController } from '@ionic/vue';
import { caretForwardCircle, close, heart, trash, share } from 'ionicons/icons';
export default defineComponent({
  name: 'Tab2Page',
  components: {
    IonContent,
    IonCard,
    IonCardContent,
    IonCardHeader,
    IonCardTitle,
    IonButton
  },
  data: () => ({
    spot: '',
    markers: []
  }),

  setup() {},

  async created() {
    const counter = useCounterStore();
    console.log(counter.count);
    const {
      data: events
    } = await supabase.from('events').select('description,name').eq('id', counter.count);
    console.log(events);
    this.spot = events;
  },

  methods: {
    async presentActionSheet() {
      const actionSheet = await actionSheetController.create({
        header: 'Albums',
        cssClass: 'my-custom-class',
        buttons: [{
          text: 'Delete',
          role: 'destructive',
          icon: trash,
          id: 'delete-button',
          data: {
            type: 'delete'
          },
          handler: () => {
            console.log('Delete clicked');
          }
        }, {
          text: 'Share',
          icon: share,
          data: 10,
          handler: () => {
            console.log('Share clicked');
          }
        }, {
          text: 'Play (open modal)',
          icon: caretForwardCircle,
          data: 'Data value',
          handler: () => {
            console.log('Play clicked');
          }
        }, {
          text: 'Favorite',
          icon: heart,
          handler: () => {
            console.log('Favorite clicked');
          }
        }, {
          text: 'Cancel',
          icon: close,
          role: 'cancel',
          handler: () => {
            console.log('Cancel clicked');
          }
        }]
      });
      await actionSheet.present();
      const {
        role,
        data
      } = await actionSheet.onDidDismiss();
      console.log('onDidDismiss resolved with role and data', role, data);
    }

  }
});
