import { defineComponent } from 'vue';
import { useCounterStore } from '@/stores/counter';
import { supabase } from "@/supabase";
import { IonContent, IonCard, IonCardContent, IonCardHeader, IonCardTitle, IonButton, actionSheetController, IonIcon } from '@ionic/vue';
export default defineComponent({
  name: 'Tab2Page',
  components: {
    IonContent,
    IonCard,
    IonCardContent,
    IonCardHeader,
    IonCardTitle,
    IonButton
  },
  data: () => ({
    spot: '',
    markers: []
  }),

  setup() {},

  async created() {
    const counter = useCounterStore();
    console.log(counter.count);
    const {
      data: events
    } = await supabase.from('events').select('description,name,status').eq('id', counter.count);
    console.log(events);
    this.spot = events;
  },

  methods: {
    async presentActionSheet() {
      const actionSheet = await actionSheetController.create({
        header: 'Status',
        cssClass: 'my-custom-class',
        buttons: [{
          text: 'Heute noch nicht besucht',
          data: 10,
          handler: () => {
            console.log('Share clicked');
            this.changeStatus(1);
          }
        }, {
          text: 'Für heute schon alles gerettet',
          data: 'Data value',
          handler: () => {
            console.log('Play clicked');
            this.changeStatus(2);
          }
        }, {
          text: 'Hier gibts noch was',
          handler: () => {
            console.log('Favorite clicked');
            this.changeStatus(3);
          }
        }, {
          text: 'Abbrechen',
          role: 'cancel',
          handler: () => {
            console.log('Abbrechen clicked');
          }
        }]
      });
      await actionSheet.present();
      const {
        role,
        data
      } = await actionSheet.onDidDismiss();
      console.log('onDidDismiss resolved with role and data', role, data);
    },

    async changeStatus(future_status) {
      console.log(future_status);
      const counter = useCounterStore();
      const {
        data: error
      } = await supabase.from('events').update({
        status: future_status
      }).eq('id', counter.count);
      console.log(error);
      const {
        data: events
      } = await supabase.from('events').select('description,name,status').eq('id', counter.count);
      console.log(events);
      this.spot = events;
    }

  }
});
