import { defineComponent } from 'vue';
import { IonPage, IonHeader, IonToolbar, IonTitle, IonContent } from '@ionic/vue';
import "leaflet/dist/leaflet.css";
import { LMap, LTileLayer, LMarker, LPopup } from "@vue-leaflet/vue-leaflet";
import { supabase } from "@/supabase";
import { modalController } from '@ionic/vue';
import Modal from './SpotModal.vue';
export default defineComponent({
  name: 'Tab2Page',
  components: {
    IonHeader,
    IonToolbar,
    IonTitle,
    IonContent,
    IonPage,
    LMap,
    LTileLayer,
    LMarker,
    LPopup
  },

  setup() {},

  async created() {
    const {
      data: events
    } = await supabase.from('events').select('coordinates,name');
    this.populate_markers_array(events);
    this.loaded = true;
  },

  data: () => ({
    spot: false,
    zoomControl: false,
    loaded: true,
    markers: []
  }),
  methods: {
    populate_markers_array: function (marker_objects) {
      console.log(marker_objects);
      this.markers = marker_objects.map(m => {
        return {
          name: m.name,
          coord: m.coordinates.split(', ').map(c => parseFloat(c))
        };
      });
    },

    async openModal() {
      const modal = await modalController.create({
        component: Modal,
        initialBreakpoint: 0.5,
        breakpoints: [0, 0.5, 1]
      });
      return modal.present();
    }

  }
});
