import { defineComponent } from 'vue';
import { IonPage, IonHeader, IonToolbar, IonTitle, IonContent, modalController } from '@ionic/vue';
import "leaflet/dist/leaflet.css";
import { LMap, LTileLayer, LMarker } from "@vue-leaflet/vue-leaflet";
import { supabase } from "@/supabase";
import Modal from './SpotModal.vue';
import { useCounterStore } from '@/stores/counter';
export default defineComponent({
  name: 'Tab2Page',
  components: {
    IonHeader,
    IonToolbar,
    IonTitle,
    IonContent,
    IonPage,
    LMap,
    LTileLayer,
    LMarker
  },

  setup() {},

  async created() {
    const {
      data: events
    } = await supabase.from('events').select('coordinates,id');
    this.populate_markers_array(events);
    this.loaded = true;
  },

  data: () => ({
    spot: false,
    zoomControl: false,
    loaded: true,
    markers: []
  }),
  methods: {
    populate_markers_array: function (marker_objects) {
      this.markers = marker_objects.map(m => {
        return {
          name: m.name,
          id: m.id,
          coord: m.coordinates.split(', ').map(c => parseFloat(c))
        };
      });
      console.log(this.markers);
    },

    async openModal(id) {
      const counter = useCounterStore();
      counter.count = id; // console.log(counter.count)

      const modal = await modalController.create({
        component: Modal,
        initialBreakpoint: 0.5,
        breakpoints: [0, 0.5, 1],
        username: "matt"
      });
      return modal.present();
    }

  }
});
